import colors from './colors.js';

const utils = {
    randomize: (size, unique) => {
        let data = [];
        if(unique){
            for(let i=1; i<=size; i++){data.push({x: i, y: i, color: colors.default})};
        }
        else{
            for(let i=1; i<=size; i++){data.push({x: i, y:  Math.floor(Math.random() * size + 1), color: colors.default})};
        }

        for(let i=data.length-1; i>=0; i--){
            let rand = Math.floor(Math.random() * (i + 1));
            let temp = data[rand].y;
            data[rand].y = data[i].y;
            data[i].y = temp;
        }

        return data;
    },

    swap: (values, i, j) => {
        let temp = values[i];
        values[i] = values[j];
        values[j] = temp;

        return values;
    }
}

export default utils;
