import React from 'react';
import '../styles/App.css';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';

import ControlPanel from './ControlPanel';
import GraphDisplay from './GraphDisplay';

function App() {
  return (
    <div className="App">
    <Box height="98vh"> 
      <Grid 
        container 
        spacing={0}
        direction="column"
        justify="center"
        align-items="stretch"
        style={{height: "100%"}}
      >
        <Grid item xs={9} style={{ borderStyle: "solid", borderColor:"#ffaa00", borderWidth: "10px", backgroundColor:"#cfe8fc", height: "75%"}}>
          <GraphDisplay />
        </Grid>
        <Grid item xs={3} style={{ borderStyle: "solid", borderColor:"#00aaff", borderWidth: "10px", backgroundColor:"#ffe8fc", height: "25%"}}>
          <ControlPanel />
        </Grid>
      </Grid>
    </Box>
    </div>
  );
}

export default App;
