import React from 'react';
import '../styles/App.css';
import { Box, Grid } from '@material-ui/core';

import ControlPanel from './ControlPanel';
import DisplayArea from './DisplayArea';

function App() {

  const data = [
    {x: 0, y: 9},
    {x: 1, y: 5},
    {x: 2, y: 4},
    {x: 3, y: 9},
    {x: 4, y: 1},
    {x: 5, y: 7},
    {x: 6, y: 6},
    {x: 7, y: 3},
    {x: 8, y: 2},
    {x: 9, y: 5}
];

  return (
    <div className="App">
    <Box height="98vh"> 
      <Grid 
        container
        direction="row"
        style={{height: "100%"}}
      >
        <Grid item xs={12} 
          style={{ backgroundColor:"#ffffff", height: "75%"}}
        >
          <DisplayArea data={data} />
        </Grid>
        <Grid item xs={12} 
          style={{ backgroundColor: "#d4e7ff", height: "25%" }}
        >
          <ControlPanel />
        </Grid>
      </Grid>
    </Box>
    </div>
  );
}

export default App;
