import React from 'react';
import '../styles/App.css';
import { Box, Grid } from '@material-ui/core';

import ControlPanel from './ControlPanel';
import DisplayArea from './DisplayArea';

function App() {
  return (
    <div className="App">
    <Box height="98vh"> 
      <Grid 
        container
        direction="row"
        style={{height: "100%"}}
      >
        <Grid item xs={12} 
          style={{ backgroundColor:"#ffffff", height: "75%"}}
        >
          <DisplayArea />
        </Grid>
        <Grid item xs={12} 
          style={{ backgroundColor: "#d4e7ff", height: "25%" }}
        >
          <ControlPanel />
        </Grid>
      </Grid>
    </Box>
    </div>
  );
}

export default App;
