import React from 'react';
import '../styles/App.css';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';

import ControlPanel from './ControlPanel';
import GraphDisplay from './GraphDisplay';

function App() {
  return (
    <div className="App">
    <Box height="98vh"> 
      <Grid 
        container 
        spacing={0}
        direction="row"
        justify="center"
        align-items="stretch"
        style={{height: "100%"}}
      >
        <Grid item xs={12} style={{backgroundColor:"#cfe8fc", height: "75%"}}>
          <GraphDisplay />
        </Grid>
        <Grid item xs={12} style={{borderColor:"#0fe8fc", borderWidth: "5px", height: "25%"}}>
          <ControlPanel />
        </Grid>
      </Grid>
    </Box>
    </div>
  );
}

export default App;
