import React from 'react';
import { Grid, Typography } from '@material-ui/core';

import MainControls from './MainControls';
import DataControls from './DataControls';
import AlgoControls from './AlgoControls';

function ControlPanel() {

    return(
        <Grid 
            container 
            direction="row"
            justify="center"
            style={{height: "100%"}}
        >
            <Grid item xs={4} style={{borderColor:"#000000", borderStyle: "solid", borderWidth: "2px"}}>
                <DataControls/>
            </Grid>
            <Grid item xs={4} md={3} lg={2} style={{borderColor:"#000000", borderStyle: "solid", borderWidth: "2px"}}>
                <Grid 
                    container 
                    spacing={0}
                    direction="row"
                    style={{height: "100%"}}
                >
                    <Grid item xs={12} style={{backgroundColor:"#ffffff"}}>
                        <Typography variant="h5">Sorting Algorithm Visualizer</Typography>
                        <Typography variant="subtitle2">By Alex Salmi</Typography>
                    </Grid>
                    <Grid item xs={12}>
                        <MainControls/>
                    </Grid>
                </Grid>
            </Grid>
            <Grid item xs={4} style={{borderColor:"#000000", borderStyle: "solid", borderWidth: "2px"}}>
                <AlgoControls/>
            </Grid>
        </Grid>
    );
}

export default ControlPanel;
