import React, {useState} from 'react';
import '../styles/App.css';
import { Box, Grid } from '@material-ui/core';

import ControlPanel from './ControlPanel';
import DisplayArea from './DisplayArea';

import utils from '../utils.js';

function App() {
  const [size,   setSize]   = useState(250);
  const [unique, setUnique] = useState(false);
  const [data,   setData]   = useState(utils.randomize(size, unique));
  const [algo,   setAlgo]   = useState('insertion');
  const [delay,  setDelay]  = useState(500);
  const [steps,  setSteps]  = useState(utils.getSolutionSteps(data, algo));

  const randomizeData = (s = size, u = unique) => {
    setData(utils.randomize(s, u));
    let solSteps = utils.getSolutionSteps(data, algo);
    setSteps(solSteps);
  };
  const toggleUnique = (value) => {
    setUnique(value);
    randomizeData(size, value);
  };
  const changeAlgo = (value) => {
    setAlgo(value);
    let solSteps = utils.getSolutionSteps(data, algo);
    setSteps(solSteps);
  }

  const solve = () => {
  }

  return (
    <div className="App">
    <Box height="98vh"> 
      <Grid 
        container
        direction="row"
        style={{height: "100%"}}
      >
        <Grid item xs={12} 
          style={{ height: "75%"}}
        >
          <DisplayArea data={data} />
        </Grid>
        <Grid item xs={12} 
          style={{ height: "25%" }}
        >
          <ControlPanel 
            randomize={randomizeData} 
            size={size}
            setSize={setSize}
            unique={unique}
            setUnique={toggleUnique}
            algo={algo}
            setAlgo={changeAlgo}
            delay={delay}
            setDelay={setDelay}
            solve={solve}
          />
        </Grid>
      </Grid>
    </Box>
    </div>
  );
}

export default App;
