import React, {useState} from 'react';
import '../styles/App.css';
import { Box, Grid } from '@material-ui/core';

import ControlPanel from './ControlPanel';
import DisplayArea from './DisplayArea';

import utils from '../utils.js';

function App() {
  const [size, setSize] = useState(100);
  const [unique, setUnique] = useState(true);
  const [data, setData] = useState(utils.randomize(size, unique));
  const [algo, setAlgo] = useState('insertion');

  const randomizeData = (u = unique, s = size) => {
    setData(utils.randomize(s, u))
  };
  const toggleUnique = (value) => {
    setUnique(value);
    randomizeData(value);
  };

  return (
    <div className="App">
    <Box height="98vh"> 
      <Grid 
        container
        direction="row"
        style={{height: "100%"}}
      >
        <Grid item xs={12} 
          style={{ height: "75%"}}
        >
          <DisplayArea data={data} />
        </Grid>
        <Grid item xs={12} 
          style={{ height: "25%" }}
        >
          <ControlPanel 
            randomize={randomizeData} 
            size={size}
            setSize={setSize}
            unique={unique}
            setUnique={toggleUnique}
            algo={algo}
            setAlgo={setAlgo}
          />
        </Grid>
      </Grid>
    </Box>
    </div>
  );
}

export default App;
