import colors from './colors.js';
import algorithms from './algorithms.js'

const utils = {
    randomize: (size, unique) => {
        let data = [];
        if(unique){
            for(let i=1; i<=size; i++){
                data.push({x: i, y: i, color: colors.default})
            };
        }
        else{
            for(let i=1; i<=size; i++){
                data.push({x: i, y:  Math.floor(Math.random() * size + 1), color: colors.default})
            };
        }

        for(let i=data.length-1; i>=0; i--){
            let rand = Math.floor(Math.random() * (i + 1));
            let temp = data[rand].y;
            data[rand].y = data[i].y;
            data[i].y = temp;
        }

        return data;
    },

    swap: (values, i, j) => {
        let temp = values[i];
        values[i] = values[j];
        values[j] = temp;

        return values;
    },

    getSolutionSteps: (data, algo) => {
        let values = data.map(item => item.y);
        let steps = [];

        switch (algo) {
            case 'insertion':
                steps = algorithms.insertion(values);
                break;
            default:
                break;

        }

        steps.push({
            selected: [],
            changedValues: []
        });

        return steps;
    },

    performStep: (data, step) => {
        for(let i=0; i<data.length; i++){
            data[i].color = colors.default;
        }

        for(let i=0; i<step.colors.length; i++){
            let ind = step.colors[i].ind;
            data[ind].color = step.colors[i].color
        }

        for(let i=0; i<step.changedValues.length; i++){
            let ind = step.changedValues[i].ind
            data[ind].y = step.changedValues[i].value;
        }

        return data;
    },

    performInverseStep: (data, steps, stepInd) => {
        // If previous step did not contain a value swap, 
        // perform the step before that to get the selected colors right
        if(steps[stepInd].changedValues.length === 0){
          data = utils.performStep(data, steps[stepInd-1]);
        }
        else{
            for(let i=0; i<data.length; i++){
                if(steps[stepInd].selected.includes(i)){
                    data[i].color = colors.selected;
                }
                else{
                    data[i].color = colors.default;
                }
            }

            if(steps[stepInd].changedValues.length === 2){
                let ind0 = steps[stepInd].changedValues[0].ind;
                data[ind0].y = steps[stepInd].changedValues[1].value;

                let ind1 = steps[stepInd].changedValues[1].ind;
                data[ind1].y = steps[stepInd].changedValues[0].value;
            }
        }

        return data;
    }
}

export default utils;
