const utils = {
    randomize: (size, unique) => {
        let values = [];
        console.log(size)
        if(unique){
            for(let i=1; i<=size; i++){values.push({x: i, y: i, color: "lightblue"})};
        }
        else{
            for(let i=1; i<=size; i++){values.push({x: i, y:  Math.floor(Math.random() * size + 1), color: "lightblue"})};
        }
        for(let i=values.length-1; i>=0; i--){
            let rand = Math.floor(Math.random() * (i + 1));
            let temp = values[rand].y;
            values[rand].y = values[i].y;
            values[i].y = temp;
        }
        console.log(values);
        return values;
    }
}

export default utils;
