export default class SolveStep {
    constructor(type){
        this.type = type;
    }

    colors = [];
    swap = [];
    sorted = {};

    addColor(index, color){
        this.colors.push({
            ind: index, 
            color: color
        });
    }

    addSwap(ind0, val0, ind1, val1){
        this.swap.push({
            ind: ind0,
            value: val0
        });
        this.swap.push({
            ind: ind1,
            value: val1
        });
    }

    setSorted(ind, lessThan){
        this.sorted = {
            ind: ind, 
            lessThan: lessThan
        }
    }
}
