import colors from './colors.js';
import algorithms from './algorithms.js'

const utils = {
    randomize: (size, unique) => {
        let data = [];
        if(unique){
            for(let i=1; i<=size; i++){
                data.push({x: i, y: i, color: colors.default})
            };
        }
        else{
            for(let i=1; i<=size; i++){
                data.push({x: i, y:  Math.floor(Math.random() * size + 1), color: colors.default})
            };
        }

        for(let i=data.length-1; i>=0; i--){
            let rand = Math.floor(Math.random() * (i + 1));
            let temp = data[rand].y;
            data[rand].y = data[i].y;
            data[i].y = temp;
        }

        return data;
    },

    swap: (values, i, j) => {
        let temp = values[i];
        values[i] = values[j];
        values[j] = temp;

        return values;
    },

    getSolutionSteps: (data, algo) => {
        let values = data.map(item => item.y);
        let steps = [];

        switch (algo) {
            case 'insertion':
                steps = algorithms.insertion(values);
                break;
            default:
                break;

        }

        steps.push({
            selected: [],
            changedValues: []
        });

        return steps;
    },

    performStep: (data, steps, ind) => {
        for(let i=0; i<data.length; i++){
            if(steps[ind].selected.includes(i)){
                data[i].color = colors.selected;
            }
            else{
                data[i].color = colors.default;
            }
        }

        for(let i=0; i<steps[ind].changedValues.length; i++){
            let ind = steps[ind].changedValues[i].ind
            data[ind].y = steps[ind].changedValues[i].value;
        }

        return data;
    },

    performInverseStep: (data, step) => {
        for(let i=0; i<data.length; i++){
            if(step.selected.includes(i)){
                data[i].color = colors.selected;
            }
            else{
                data[i].color = colors.default;
            }
        }

        if(step.changedValues.length === 2){
            let ind0 = step.changedValues[0].ind;
            data[ind0].y = step.changedValues[1].value;

            let ind1 = step.changedValues[1].ind;
            data[ind1].y = step.changedValues[0].value;
        }

        return data;
    }
}

export default utils;
