import React from 'react';
import { Grid, Paper, Typography } from '@material-ui/core';

import MainControls from './MainControls';
import DataControls from './DataControls';
import AlgoControls from './AlgoControls';

function ControlPanel(props) {

    return(
        <Paper elevation="10">
            <Grid 
                container 
                direction="row"
                justify="space-around"
                align-items="center"
            >
                <Grid item xs={4} >
                    <DataControls 
                        randomize={props.randomize}
                        size={props.size}
                        setSize={props.setSize}
                        unique={props.unique}
                        setUnique={props.setUnique}
                    />
                </Grid>
                <Grid item xs={4} md={3} lg={2} >
                    <Paper 
                        style={{height:"100%"}}
                        elevation={1}    
                    >
                        <Grid 
                            container 
                            spacing={0}
                            direction="row"
                            style={{height: "100%"}}
                        >
                            <Grid item xs={12}>
                                <Typography variant="h5">Sorting Algorithm Visualizer</Typography>
                                <Typography variant="subtitle2">By Alex Salmi</Typography>
                            </Grid>
                            <Grid item xs={12}>
                                <MainControls randomize={props.randomize}/>
                            </Grid>
                        </Grid>
                    </Paper>
                </Grid>
                <Grid item xs={4} >
                    <AlgoControls
                        algo={props.algo}
                        setAlgo={props.setAlgo}    
                    />
                </Grid>
            </Grid>
        </Paper>
    );
}

export default ControlPanel;
