import React from 'react';
import '../styles/App.css';
import { Box, Grid } from '@material-ui/core';

import ControlPanel from './ControlPanel';
import GraphDisplay from './GraphDisplay';

function App() {
  return (
    <div className="App">
    <Box height="98vh"> 
      <Grid 
        container
        direction="row"
        style={{height: "100%"}}
      >
        <Grid item xs={12} style={{ backgroundColor:"#cfe8fc", height: "75%"}}>
          <GraphDisplay />
        </Grid>
        <Grid item xs={12} style={{ height: "25%", backgroundColor: "#dde4ed"}}>
          <ControlPanel />
        </Grid>
      </Grid>
    </Box>
    </div>
  );
}

export default App;
