import React, {useState} from 'react';
import '../styles/App.css';
import { Box, Grid } from '@material-ui/core';

import ControlPanel from './ControlPanel';
import DisplayArea from './DisplayArea';

import utils from '../utils.js';

function App() {
  const [size,    setSize]   =  useState(250);
  const [unique,  setUnique] =  useState(false);
  const [data,    setData]   =  useState(utils.randomize(size, unique));
  const [algo,    setAlgo]   =  useState('insertion');
  const [delay,   setDelay]  =  useState(500);
  const [steps,   setSteps]  =  useState(utils.getSolutionSteps(data, algo));
  const [stepInd, setStepInd] = useState(0);

  const randomizeData = (s = size, u = unique) => {
    let newData = utils.randomize(s, u)
    setData(newData);
    let solSteps = utils.getSolutionSteps(newData, algo);
    setSteps(solSteps);
  };
  const toggleUnique = (value) => {
    setUnique(value);
    randomizeData(size, value);
  };
  const changeAlgo = (value) => {
    setAlgo(value);
    let solSteps = utils.getSolutionSteps(data, value);
    setSteps(solSteps);
  }

  const solve = () => {
    let i = stepInd;
    let interval = setInterval(() => {
      let newData = utils.performStep(data, steps[i]);

      console.log(newData);

      setData(newData);
      i++;

      if(i === 5){
        clearInterval(interval);
      }
    }, delay);

  }

  return (
    <div>
    <Box height="98vh"> 
      <Grid 
        container
        direction="row"
        style={{height: "100%"}}
      >
        <Grid item xs={12} 
          style={{ height: "75%"}}
        >
          <DisplayArea data={data} />
        </Grid>
        <Grid item xs={12} 
          style={{ height: "25%" }}
        >
          <ControlPanel 
            randomize={randomizeData} 
            size={size}
            setSize={setSize}
            unique={unique}
            setUnique={toggleUnique}
            algo={algo}
            setAlgo={changeAlgo}
            delay={delay}
            setDelay={setDelay}
            solve={solve}
          />
        </Grid>
      </Grid>
    </Box>
    </div>
  );
}

export default App;
