import React from 'react';
import { Grid, Button } from '@material-ui/core';

function MainControls() {
    return (
        <Grid 
            container 
            spacing={0}
            direction="row"
            justify="center"
            align-items="stretch"
            style={{height: "100%"}}
        >
            <Grid item xs={4} style={{backgroundColor:"#3c2b1a"}}>
                <Button variant="contained" color="primary"> -1 </Button>
            </Grid>
            <Grid item xs={4} style={{backgroundColor:"#001122"}}>
                <Button variant="contained" color="primary">Play</Button>
            </Grid>
            <Grid item xs={4} style={{backgroundColor:"#ffeedd"}}>
               <Button variant="contained" color="primary"> +1 </Button>
            </Grid>
        </Grid>
    );
}

export default MainControls;
